import React, { useState } from "react";
import { Link } from "react-router-dom";
import styles from "./menu.module.css";
import { FiChevronDown } from 'react-icons/fi';

function Menu() {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  function toggleDropdown() {
    setDropdownOpen(!dropdownOpen);
  }

  return (
    <div className={styles.header}>
      <div className={styles.menu}>
        <div>Início</div>
        <div>Sobre</div>
        <div>Contato</div>
        <div>Histórico</div>
        <div>More...</div>
      </div>
      <div className={styles.nameMenu}>
        <h1>Vitoria Garavazzo</h1>
        <h4>Estética Avançada</h4>
      </div>
      <div className={`${styles.menuLogin} ${dropdownOpen ? styles.open : ''}`}>
        <div>Icon</div>
        <div>Foto</div>
        <div className={styles.dropdown}>
          <FiChevronDown className={styles.iconButton} onClick={toggleDropdown} />
          {dropdownOpen && (
            <div className={styles.dropdownContent}>
              <Link to="/login" className={styles.dropdownLink}>Login</Link>
              <Link to="/login" className={styles.dropdownLink}>Register</Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Menu;
