import React, { useState, useEffect } from "react";
import styles from "./body.module.css";
import Img1 from '../../img/Img1Wix.webp';

function Body() {
  const [conteudo1P1, setConteudo1P1] = useState("01");
  const conteudo1P2 = "10% de Desconto com o cupom: vg123";
  const conteudo2P1 = "02";
  const conteudo2P2 = "Descontos em compras acima de R$200";
  const conteudo3P1 = "03";
  const conteudo3P2 = "A primeira consulta é por nossa conta";

  useEffect(() => {
    const conteudos = ["01", "02", "03"]; // Lista de conteúdos a serem alternados
    let currentIndex = 0;

    const interval = setInterval(() => {
      // Atualizar o conteúdo a cada 5 segundos
      setConteudo1P1(conteudos[currentIndex]);
      currentIndex = (currentIndex + 1) % conteudos.length;
    }, 5000);

    return () => {
      clearInterval(interval); // Limpar o intervalo quando o componente é desmontado
    };
  }, []);

  return (
    <div className={styles.body}>
      <img src={Img1} className={styles.imgBody} />
      <div className={styles.contentImg1}>
        <p className={styles.contentP1Text}>
          {`${conteudo1P1}`}
        </p>
        <p className={styles.contentP2Text}>
          {"/"}
        </p>
        <p className={styles.contentP3Text}>
          {`${conteudo1P2}`}
        </p>
        <div className={styles.contentButton}>
          <p className={styles.textButton}>Comprar</p>
        </div>
      </div>
    </div>
  );
}

export default Body;
