import React from 'react';
import styles from './table.module.css';

interface TableRow {
  [key: string]: any;
}

interface TableProps {
  data: TableRow[];
  onDelete: (index: number) => void; // Função para deletar um item da tabela
  onEdit: (index: number) => void; // Função para editar um item da tabela
}

const Table: React.FC<TableProps> = ({ data, onDelete, onEdit }) => {
  return (
    <div className={styles.tableContainer}>
      <table className={styles.table}>
        <thead>
          <tr>
            {data.length > 0 && Object.keys(data[0]).map((key) => (
              <th key={key}>{key}</th>
            ))}
            <th>Ações</th> {/* Coluna para botões de ação */}
          </tr>
        </thead>
        <tbody>
          {data.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {Object.values(row).map((cell, cellIndex) => (
                <td key={cellIndex}>{cell}</td>
              ))}
              <td>
                <button onClick={() => onDelete(rowIndex)}>Deletar</button>
                <br/>
                <button onClick={() => onEdit(rowIndex)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
