import React, { useState } from 'react';
import styles from './procedimentos.module.css';
import axios from 'axios';

const Cadastro: React.FC = () => {
  const [nome, setNome] = useState<string>('');
  const [valor, setValor] = useState<string>('');
  const [descricao, setDescricao] = useState<string>('');
  const [tipoProcedimento, setTipoProcedimento] = useState<number>(1);
  const [imagem, setImagem] = useState<File | null>(null);

  const handleImagemChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      if (e.target.files && e.target.files[0].type.startsWith('image/')) {
        setImagem(e.target.files[0]);
      } else {
        setImagem(null);
      }
    }
  };

  const handleClickCadastro = () => {
    console.log('Cadastro');

    const filter = {
      nome,
      descricao,
      valor,
      inserirArquivo: imagem,
      tipoProcedimento,
    }

    console.log(filter)

    const apiUrl = 'http://localhost:5082/api/Procedimento';

    axios.post(apiUrl, filter)
      .then((response) => {
        console.log(response.data)
      })
      .catch((error) => {
        console.error('Erro ao cadastrar procedimento:', error);
      });

  };

  return (
    <div>
      <div className={styles.cadastroContainer}>
        <div className={styles.cadastroBox}>
          <h2>Cadastro de Procedimentos</h2>
          <div className={styles.formGroup}>
            <label>Nome:</label>
            <input
              type="text"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
            />
          </div>
          <div className={styles.formGroup}>
            <label>Descrição:</label>
            <input
              type="text"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className={styles.formGroup}>
            <label>Valor:</label>
            <input
              type="number"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
          </div>
          <div className={styles.formGroup}>
            <label>Tipo de Procedimento:</label>
            <select
              value={tipoProcedimento}
              onChange={(e) => {
                if (e.target.value === "1") {
                  setTipoProcedimento(1);
                } else if (e.target.value === "2") {
                  setTipoProcedimento(2);
                }
              }}
            >
              <option className={styles.option} value="1">Corporal</option>
              <option className={styles.option} value="2">Facial</option>
            </select>
          </div>
          <div className={styles.formGroup}>
            <label>Imagem:</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImagemChange}
            />
          </div>
          <button className={styles.cadastroButton} onClick={handleClickCadastro}>
            Cadastrar
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cadastro;
