import React, { useState } from 'react';
import styles from './procedimentos.module.css';

const Cadastro: React.FC = () => {
  const [nome, setNome] = useState('');
  const [preco, setPreco] = useState('');
  const [descricao, setDescricao] = useState('');
  const [tempo, setTempo] = useState('');
  const [data, setData] = useState<any>(null);
  const [imagem, setImagem] = useState<File | null>(null);

  const handleImagemChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      if (e.target.files && e.target.files[0].type.startsWith('image/')) {
        setImagem(e.target.files[0]);
        console.log('teste');
      } else {
        setImagem(null);
      }
    }
  };

  const handleClickCadastro = () => {
    console.log('Cadastro');

    const formData = new FormData();
    formData.append('nome', nome);
    formData.append('preco', preco);
    formData.append('descricao', descricao);
    formData.append('tempo', tempo);

    console.log(imagem)


    if (imagem !== null) {
      formData.append('imagem', imagem, imagem.name);
    }

    const filter = {
      nome,
      preco,
      descricao,
      tempo
    }

    console.log(filter)

    // const apiUrl = 'http://localhost:5080/Users/Details/5';

    // axios.get(apiUrl)
    //   .then((response) => {
    //     setData(response.data);
    //     console.log(response.data)
    //   })
    //   .catch((error) => {
    //     console.error('Erro ao buscar dados do back-end:', error);
    //   });

  };

  return (
    <div>
      <div className={styles.cadastroContainer}>
        <div className={styles.cadastroBox}>
          <h2>Cadastro de Procedimentos</h2>
          <div className={styles.formGroup}>
            <label>Nome:</label>
            <input
              type="text"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
            />
          </div>
          <div className={styles.formGroup}>
            <label>Descrição:</label>
            <input
              type="text"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className={styles.formGroup}>
            <label>Valor:</label>
            <input
              type="text"
              value={preco}
              onChange={(e) => setPreco(e.target.value)}
            />
          </div>
          <div className={styles.formGroup}>
            <label>Tempo:</label>
            <input
              type="text"
              value={tempo}
              onChange={(e) => setTempo(e.target.value)}
            />
          </div>
          <div className={styles.formGroup}>
            <label>Imagem:</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImagemChange}
            />
          </div>
          <button className={styles.cadastroButton} onClick={handleClickCadastro}>
            Cadastrar
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cadastro;
