import React from 'react';
import styles from './table.module.css';

interface TableRow {
  [key: string]: any;
}

interface TableProps {
  data: TableRow[];
  onDelete: (index: number) => void;
  onEdit: (index: number) => void;
}

const Table: React.FC<TableProps> = ({ data, onDelete, onEdit }) => {
  // Defina as chaves que deseja exibir na tabela (excluindo a chave 'id')
  const visibleKeys = Object.keys(data[0]).filter(key => key !== 'id');

  return (
    <div className={styles.tableContainer}>
      <table className={styles.table}>
        <thead>
          <tr>
            {visibleKeys.map((key) => (
              <th key={key}>{key}</th>
            ))}
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row) => (
            <tr key={row.id}>
              {visibleKeys.map((key) => (
                <td key={key}>{row[key]}</td>
              ))}
              <td>
                <button onClick={() => onDelete(row.id)}>Deletar</button>
                <button className={styles.buttonEdit} onClick={() => onEdit(row.id)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
